mapper.update:
update tb_user
set id = #{id}, password = #{password}, user_name = #{userName}, mobile = #{mobile}, email = #{email}, birth_date = #{birthDate}, location = #{location}
where user_number = #{userNumber}

UPDATE tb_user
   SET beach_number = #{beachNumber}
 WHERE user_number = #{userNumber}

UPDATE tb_user
   SET beach_number = NULL
 WHERE user_number = #{userNumber}
